        /// <summary>
        /// html from wordfinder given letters
        /// </summary>
        /// <param name="html"></param>
        /// <returns></returns>
        public static string getHtmlWF(string ltrs)
        {
            string sHTML = "";
            const string wf1 = @"http://wordfinder.yourdictionary.com/unscramble/";
            using (var client = new WebClient())
            {
                string url = string.Format("{0}{1}", wf1, ltrs);
                sHTML = client.DownloadString(url);
            }

            return (sHTML);
        }

        public static string getHtmlWFResults(string html)
        {
            string sHTML = "";
            const string m1 = @" words found for letters ";
            const string m11 = @"<section class=""twelve column"">";
            const string m2 = @"</section>";

            int sPos = html.IndexOf(m11, html.IndexOf(m1)) + m11.Length;
            int ePos = html.IndexOf(m2, sPos);

            if (ePos > sPos)
            {
                sHTML = html.Substring(sPos, ePos - sPos);
            }

            return (sHTML);
        }

        // from datacol values to string array
        public static string[] wordsColToStrArray(DataTable dt,  int columnIndex)
        {
          // desired column index

            string[] results = new string[dt.Rows.Count];
            for (int index = 0; index < dt.Rows.Count; index++)
            {
                results[index] = dt.Rows[index][columnIndex].ToString();
            }

            return results;
        }

