            for (int ctr = 0; ctr < ds1.Tables.Count; ctr++)
            {
                int nL = Convert.ToInt32(ds1.Tables[ctr].TableName);
                if (wordLength == nL)
                {
                    dt1 = ds1.Tables[ctr];
                    break;
                }
            }

            return dt1;
        }

        public static List<string> getCriteriaMatches(DataTable dt1, string criteria)
        {
            List<string> matches = new List<string>();
            const int wordsColMW = 1;

            string srchPattern = criteria.Replace("-", @"\w");
            string words = string.Join(",", wordsColToStrArray(dt1, wordsColMW));

            MatchCollection patternMatches = Regex.Matches(words, srchPattern, ExpressionOptions);

            foreach (Match pMatch in patternMatches)
            {
                matches.Add(pMatch.ToString());
            }

            return matches;
        }

        public static List<string> getWsForCriteria(DataSet ds1, string[] criteria)
        {
            DataTable dt1 = null;// getTableByLength(ds1, criteria[i].Length);
            List<string> tmpFinds = new List<string>();
            List<string> finds = new List<string>();

            for (int i = 0; i < criteria.Length; i++)
            {

                dt1 = getTableByLength(ds1, criteria[i].Length);
                if (dt1 != null)
                {
                    tmpFinds = getCriteriaMatches(dt1, criteria[i]);
                    finds.AddRange(tmpFinds);
                }

            }

            return finds;

        }

